<a name="l00185"></a><a class="code" href="group__hwlocality__helper__traversal__basic.php#gac574b7b06d2d937002dd538e08dbd554">00185</a> <a class="code" href="group__hwlocality__helper__traversal__basic.php#gac574b7b06d2d937002dd538e08dbd554" title="Returns the common parent object to objects lvl1 and lvl2.">hwloc_get_common_ancestor_obj</a> (<a class="code" href="group__hwlocality__topology.php#ga9d1e76ee15a7dee158b786c30b6a6e38" title="Topology context.">hwloc_topology_t</a> topology , <a class="code" href="structhwloc__obj.php" title="Structure of a topology object.">hwloc_obj_t</a> obj1, <a class="code" href="structhwloc__obj.php" title="Structure of a topology object.">hwloc_obj_t</a> obj2)
<a name="l00186"></a>00186 {
<a name="l00187"></a>00187   <span class="comment">/* the loop isn&#39;t so easy since intermediate ancestors may have</span>
<a name="l00188"></a>00188 <span class="comment">   * different depth, causing us to alternate between using obj1-&gt;parent</span>
<a name="l00189"></a>00189 <span class="comment">   * and obj2-&gt;parent. Also, even if at some point we find ancestors of</span>
<a name="l00190"></a>00190 <span class="comment">   * of the same depth, their ancestors may have different depth again.</span>
<a name="l00191"></a>00191 <span class="comment">   */</span>
<a name="l00192"></a>00192   <span class="keywordflow">while</span> (obj1 != obj2) {
<a name="l00193"></a>00193     <span class="keywordflow">while</span> (obj1-&gt;<a class="code" href="structhwloc__obj.php#a9d82690370275d42d652eccdea5d3ee5" title="Vertical index in the hierarchy.">depth</a> &gt; obj2-&gt;<a class="code" href="structhwloc__obj.php#a9d82690370275d42d652eccdea5d3ee5" title="Vertical index in the hierarchy.">depth</a>)
<a name="l00194"></a>00194       obj1 = obj1-&gt;<a class="code" href="structhwloc__obj.php#adc494f6aed939992be1c55cca5822900" title="Parent, NULL if root (system object)">parent</a>;
<a name="l00195"></a>00195     <span class="keywordflow">while</span> (obj2-&gt;<a class="code" href="structhwloc__obj.php#a9d82690370275d42d652eccdea5d3ee5" title="Vertical index in the hierarchy.">depth</a> &gt; obj1-&gt;<a class="code" href="structhwloc__obj.php#a9d82690370275d42d652eccdea5d3ee5" title="Vertical index in the hierarchy.">depth</a>)
<a name="l00196"></a>00196       obj2 = obj2-&gt;<a class="code" href="structhwloc__obj.php#adc494f6aed939992be1c55cca5822900" title="Parent, NULL if root (system object)">parent</a>;
<a name="l00197"></a>00197     <span class="keywordflow">if</span> (obj1 != obj2 &amp;&amp; obj1-&gt;<a class="code" href="structhwloc__obj.php#a9d82690370275d42d652eccdea5d3ee5" title="Vertical index in the hierarchy.">depth</a> == obj2-&gt;<a class="code" href="structhwloc__obj.php#a9d82690370275d42d652eccdea5d3ee5" title="Vertical index in the hierarchy.">depth</a>) {
<a name="l00198"></a>00198       obj1 = obj1-&gt;<a class="code" href="structhwloc__obj.php#adc494f6aed939992be1c55cca5822900" title="Parent, NULL if root (system object)">parent</a>;
<a name="l00199"></a>00199       obj2 = obj2-&gt;<a class="code" href="structhwloc__obj.php#adc494f6aed939992be1c55cca5822900" title="Parent, NULL if root (system object)">parent</a>;
<a name="l00200"></a>00200     }
<a name="l00201"></a>00201   }
<a name="l00202"></a>00202   <span class="keywordflow">return</span> obj1;
<a name="l00203"></a>00203 }
