<a name="l00235"></a>00235   <span class="comment">/* FIXME: what if !root-&gt;cpuset? */</span>
<a name="l00236"></a>00236   <span class="keywordflow">if</span> (!<a class="code" href="group__hwlocality__bitmap.php#ga575c27953709a8cb9a047aae65157526" title="Test whether bitmaps bitmap1 and bitmap2 intersects.">hwloc_bitmap_intersects</a>(obj-&gt;<a class="code" href="structhwloc__obj.php#a67925e0f2c47f50408fbdb9bddd0790f" title="CPUs covered by this object.">cpuset</a>, <span class="keyword">set</span>))
<a name="l00237"></a>00237     <span class="keywordflow">return</span> NULL;
<a name="l00238"></a>00238   <span class="keywordflow">while</span> (!<a class="code" href="group__hwlocality__bitmap.php#gaae29e14a926c198e8f91e6e4790621e7" title="Test whether bitmap sub_bitmap is part of bitmap super_bitmap.">hwloc_bitmap_isincluded</a>(obj-&gt;<a class="code" href="structhwloc__obj.php#a67925e0f2c47f50408fbdb9bddd0790f" title="CPUs covered by this object.">cpuset</a>, <span class="keyword">set</span>)) {
<a name="l00239"></a>00239     <span class="comment">/* while the object intersects without being included, look at its children */</span>
<a name="l00240"></a>00240     <a class="code" href="structhwloc__obj.php" title="Structure of a topology object.">hwloc_obj_t</a> child = NULL;
<a name="l00241"></a>00241     <span class="keywordflow">while</span> ((child = <a class="code" href="group__hwlocality__helper__traversal__basic.php#gae5ef1af636849f77714e1584ba78cf9c" title="Return the next child.">hwloc_get_next_child</a>(topology, obj, child)) != NULL) {
<a name="l00242"></a>00242       <span class="keywordflow">if</span> (child-&gt;<a class="code" href="structhwloc__obj.php#a67925e0f2c47f50408fbdb9bddd0790f" title="CPUs covered by this object.">cpuset</a> &amp;&amp; <a class="code" href="group__hwlocality__bitmap.php#ga575c27953709a8cb9a047aae65157526" title="Test whether bitmaps bitmap1 and bitmap2 intersects.">hwloc_bitmap_intersects</a>(child-&gt;<a class="code" href="structhwloc__obj.php#a67925e0f2c47f50408fbdb9bddd0790f" title="CPUs covered by this object.">cpuset</a>, <span class="keyword">set</span>))
<a name="l00243"></a>00243         <span class="keywordflow">break</span>;
<a name="l00244"></a>00244     }
<a name="l00245"></a>00245     <span class="keywordflow">if</span> (!child)
<a name="l00246"></a>00246       <span class="comment">/* no child intersects, return their father */</span>
<a name="l00247"></a>00247       <span class="keywordflow">return</span> obj;
<a name="l00248"></a>00248     <span class="comment">/* found one intersecting child, look at its children */</span>
<a name="l00249"></a>00249     obj = child;
<a name="l00250"></a>00250   }
<a name="l00251"></a>00251   <span class="comment">/* obj is included, return it */</span>
<a name="l00252"></a>00252   <span class="keywordflow">return</span> obj;
<a name="l00253"></a>00253 }
