<a name="l00657"></a>00657 
<a name="l00658"></a>00658   tot_weight = 0;
<a name="l00659"></a>00659   <span class="keywordflow">for</span> (i = 0; i &lt; n_roots; i++)
<a name="l00660"></a>00660     <span class="keywordflow">if</span> (roots[i]-&gt;cpuset)
