<a name="l00245"></a>00245   <span class="comment">/* FIXME: what if !root-&gt;cpuset? */</span>
<a name="l00246"></a>00246   <span class="keywordflow">if</span> (!<a class="code" href="group__hwlocality__bitmap.php#ga575c27953709a8cb9a047aae65157526" title="Test whether bitmaps bitmap1 and bitmap2 intersects.">hwloc_bitmap_intersects</a>(obj-&gt;<a class="code" href="structhwloc__obj.php#a67925e0f2c47f50408fbdb9bddd0790f" title="CPUs covered by this object.">cpuset</a>, <span class="keyword">set</span>))
<a name="l00247"></a>00247     <span class="keywordflow">return</span> NULL;
<a name="l00248"></a>00248   <span class="keywordflow">while</span> (!<a class="code" href="group__hwlocality__bitmap.php#gaae29e14a926c198e8f91e6e4790621e7" title="Test whether bitmap sub_bitmap is part of bitmap super_bitmap.">hwloc_bitmap_isincluded</a>(obj-&gt;<a class="code" href="structhwloc__obj.php#a67925e0f2c47f50408fbdb9bddd0790f" title="CPUs covered by this object.">cpuset</a>, <span class="keyword">set</span>)) {
