<a name="l00250"></a>00250     <a class="code" href="structhwloc__obj.php" title="Structure of a topology object.">hwloc_obj_t</a> child = NULL;
<a name="l00251"></a>00251     <span class="keywordflow">while</span> ((child = <a class="code" href="group__hwlocality__helper__traversal__basic.php#gae5ef1af636849f77714e1584ba78cf9c" title="Return the next child.">hwloc_get_next_child</a>(topology, obj, child)) != NULL) {
<a name="l00252"></a>00252       <span class="keywordflow">if</span> (child-&gt;<a class="code" href="structhwloc__obj.php#a67925e0f2c47f50408fbdb9bddd0790f" title="CPUs covered by this object.">cpuset</a> &amp;&amp; <a class="code" href="group__hwlocality__bitmap.php#ga575c27953709a8cb9a047aae65157526" title="Test whether bitmaps bitmap1 and bitmap2 intersects.">hwloc_bitmap_intersects</a>(child-&gt;<a class="code" href="structhwloc__obj.php#a67925e0f2c47f50408fbdb9bddd0790f" title="CPUs covered by this object.">cpuset</a>, <span class="keyword">set</span>))
<a name="l00253"></a>00253         <span class="keywordflow">break</span>;
<a name="l00254"></a>00254     }
<a name="l00255"></a>00255     <span class="keywordflow">if</span> (!child)
<a name="l00256"></a>00256       <span class="comment">/* no child intersects, return their father */</span>
<a name="l00257"></a>00257       <span class="keywordflow">return</span> obj;
<a name="l00258"></a>00258     <span class="comment">/* found one intersecting child, look at its children */</span>
<a name="l00259"></a>00259     obj = child;
<a name="l00260"></a>00260   }
<a name="l00261"></a>00261   <span class="comment">/* obj is included, return it */</span>
<a name="l00262"></a>00262   <span class="keywordflow">return</span> obj;
<a name="l00263"></a>00263 }
